function onPlayerReady(e){e.target.playVideo()}function stopVideo(){player.stopVideo()}function pauseVideo(){for(videoPlayer in players)players[videoPlayer].pauseVideo()}function resumeVideo(){for(videoPlayer in players)players[videoPlayer].playVideo()}function initVideo(){$(".play-btn").on("click",function(e){e.preventDefault();var a=document.getElementById("video-cont"),i=a.dataset.videoId,t=a.dataset.videoTitle;createPlayer(t,i,a),$(this).animate({opacity:0},function(){$(this).css("visibility","hidden")})})}function onYouTubeIframeAPIReady(){initVideo()}function createPlayer(e,a,i){players[e]=new YT.Player(i,{videoId:a,playerVars:{iv_load_policy:3,controls:0},events:{onReady:onPlayerReady}})}var tag=document.createElement("script");tag.src="//www.youtube.com/iframe_api";var firstScriptTag=document.getElementsByTagName("script")[0];firstScriptTag.parentNode.insertBefore(tag,firstScriptTag);var players={};